mapper.select:
SELECT NVL(MAX(ROWNUM), 0)
FROM tp_priboard
WHERE user_id=#{page_id}

SELECT name 
FROM tp_account 
WHERE user_id=#{page_id}

mapper.update:
UPDATE tp_priboard 
SET view_count=view_count+1 
WHERE cont_id=#{cont_id}

UPDATE tp_priboard 
SET content_title=#{content_title}, content_content=#{content_content} 
WHERE user_id=#{user_id} AND cont_id=#{cont_id}
